INSERT INTO `uk_templet` VALUES ('402881e861da4c960161da74a89e0386', '报表XML模板', null, 'reportquery', null, '2018-02-28 11:28:53', null, null, '<?xml version=\'1.0\' ?>\r\n<Schema name=\'ukefu\'>\r\n    <#if !cube.modeltype?? || cube.modeltype!=\"1\">\r\n    <Cube name=\'${cube.name}\'>\r\n		<#if cube.sql?? && cube.sql?length gt 0>\r\n		<View alias=\'${cube.table}\'>\r\n                        <SQL>                              \r\n							${(cube.sql!\'\')?html}\r\n                        </SQL>\r\n                </View>\r\n		<#else>\r\n		<Table name=\'${cube.table}\' />\r\n                </#if>\r\n		\r\n		<#if reportModel.properties?? && reportModel.properties?size gt 0 >\r\n			<Dimension name=\'${reportModel.name}\'>\r\n				<Hierarchy hasAll=\'true\' allMemberName=\'\'>						\r\n					<#list reportModel.properties as property>\r\n						<#list cube.dimension as dimension>\r\n							<#list dimension.cubeLevel as level>\r\n							<#if property.dataid == level.id>\r\n							<Level name=\'${level.name}\'  column=\'<#if dimension.fktable?? && dimension.fktable!= \"\">${level.tableproperty.key}<#else>${level.nameAlias}</#if>\' uniqueMembers=\'${level.uniquemembers?string}\'  <#if level.parameters??>${level.parameters!\"\"}</#if>>\r\n								<#if level.attribue?? && level.attribue?length gt 0>${level.attribue!\"\"}</#if>\r\n								</Level>	\r\n							</#if>						\r\n							</#list>	\r\n						</#list>					\r\n					</#list>			\r\n				</Hierarchy>\r\n			</Dimension>        \r\n		</#if>\r\n		<#if istable?? && istable && reportModel.colproperties?? && reportModel.colproperties?size gt 0>\r\n			<Dimension name=\'${reportModel.name}_col\'>\r\n				<Hierarchy hasAll=\'true\' allMemberName=\'\'>\r\n					<#if reportModel.colproperties??>\r\n					<#list reportModel.colproperties as property>\r\n						<#list cube.dimension as dimension>\r\n							<#list dimension.cubeLevel as level>\r\n							<#if property.dataid == level.id>\r\n							<Level name=\'${level.name}\'  column=\'<#if dimension.fktable?? && dimension.fktable!= \"\">${level.tableproperty.key}<#else>${level.nameAlias}</#if>\' uniqueMembers=\'${level.uniquemembers?string}\'  <#if level.parameters??>${level.parameters!\"\"}</#if>>\r\n								<#if level.attribue?? && level.attribue?length gt 0>${level.attribue!\"\"}</#if>\r\n								</Level>	\r\n							</#if>						\r\n							</#list>	\r\n						</#list>					\r\n					</#list>\r\n					</#if>\r\n				</Hierarchy>\r\n			</Dimension>\r\n		</#if>\r\n\r\n		<#if cube.measure??>\r\n			<#list cube.measure as measure>				\r\n				<#if measure.calculatedmember != true>\r\n				<Measure name=\'${measure.name}\' <#if measure.attribue?? && measure.attribue?length gt 0><#else>column=\'${measure.nameAlias}\'</#if> aggregator=\'${measure.aggregator}\' <#if measure.formatstring != \'\'>formatString=\'${measure.formatstring!\"####\"}\'<#else>formatString=\'####\'</#if>>\r\n						<#if measure.attribue?? && measure.attribue?length gt 0>\r\n						${measure.attribue!\"\"}\r\n						</#if>\r\n				</Measure>\r\n				</#if>\r\n			</#list>\r\n        </#if>\r\n		\r\n\r\n		<#if cube.measure??>\r\n			<#list cube.measure as measure>				\r\n				<#if measure.calculatedmember == true>\r\n					<CalculatedMember name=\'${measure.name}\' dimension=\'Measures\' <#if measure.parameters??>${measure.parameters!\"\"}</#if> <#if measure.formatstring?? && measure.formatstring?length gt 0>formatString=\"${measure.formatstring!\"\"}\"</#if>>\r\n                             <#if measure.attribue?? && measure.attribue?length gt 0>${measure.attribue!\"\"}</#if>\r\n                        </CalculatedMember>\r\n				</#if>\r\n			</#list>\r\n		</#if>\r\n\r\n		</Cube>	\r\n	</#if>\r\n</Schema>', '402881e861da4c960161da7040bc0385', 'ukewo', null, null, null, null, null, '0', null, null);
